const files = ($json.stdout || '').split('\n').filter(f => f.trim());

if (files.length === 0) {
  throw new Error('No PDF files found');
}

const FILE_NUMBER = 1;
const selectedFile = files[FILE_NUMBER - 1];

console.log('Selected: ' + selectedFile);

return [{json: {
  filepath: selectedFile,
  filename: selectedFile.split('/').pop()
}}];

const filepath = $node['selectFile'].json.filepath;
const filename = $node['selectFile'].json.filename;

if ($json.error) {
  return [{json: {
    status: 'ERROR',
    message: $json.message || 'PDF.co failed',
    filename: filename
  }}];
}

const text = ($json.body || $json.text || '');
const textLower = text.toLowerCase();

// KLASIFIKACE TYPU DOKUMENTU
let docType = 'unknown';
if (textLower.includes('faktura') || textLower.includes('invoice')) docType = 'faktura';
else if (textLower.includes('účtenka') || textLower.includes('uctenka') || textLower.includes('receipt')) docType = 'uctenka';
else if (textLower.includes('výpis') || textLower.includes('vypis') || textLower.includes('statement')) docType = 'vypis';
else if (textLower.includes('smlouva') || textLower.includes('contract')) docType = 'smlouva';
else if (textLower.includes('úřední') || textLower.includes('urednik') || textLower.includes('official')) docType = 'urednik';

// NAJIT KORESPONDENTA
let correspondent = 'Unknown';
if (textLower.includes('čsob') || textLower.includes('csob')) correspondent = 'CSOB';
else if (textLower.includes('komerční banka') || textLower.includes('komercni banka')) correspondent = 'Komercni banka';
else if (textLower.includes('česká spořitelna') || textLower.includes('ceska sporitelna')) correspondent = 'Ceska sporitelna';
else if (textLower.includes('raiffeisenbank')) correspondent = 'Raiffeisenbank';
else if (textLower.includes('moneta')) correspondent = 'Moneta';
else if (textLower.includes('alza')) correspondent = 'Alza';
else if (textLower.includes('lidl')) correspondent = 'Lidl';
else if (textLower.includes('kaufland')) correspondent = 'Kaufland';
else if (textLower.includes('tesco')) correspondent = 'Tesco';
else if (textLower.includes('amazon')) correspondent = 'Amazon';

// PARSOVANI SPECIFICKYCH UDAJU PODLE TYPU
const customFields = {};
const tags = [docType];

if (correspondent !== 'Unknown') {
  tags.push(correspondent.toLowerCase().replace(/\s+/g, '-'));
}

// PARSOVANI PRO FAKTURU
if (docType === 'faktura') {
  // ICO
  const icoMatch = text.match(/IČO?:?\s*(\d{8})/i);
  if (icoMatch) {
    customFields.ico = icoMatch[1];
    tags.push('ico-' + icoMatch[1]);
  }

  // DIC
  const dicMatch = text.match(/DIČ:?\s*(CZ\d{8,10})/i);
  if (dicMatch) {
    customFields.dic = dicMatch[1];
    tags.push('dic-' + dicMatch[1].toLowerCase());
  }

  // Cislo faktury
  const invoiceMatch = text.match(/(?:číslo faktury|invoice number|faktura č\.?):?\s*([A-Z0-9\-\/]+)/i);
  if (invoiceMatch) {
    customFields.invoice_number = invoiceMatch[1];
  }

  // Variabilni symbol
  const vsMatch = text.match(/(?:VS|variabilní symbol):?\s*(\d+)/i);
  if (vsMatch) {
    customFields.variable_symbol = vsMatch[1];
    tags.push('vs-' + vsMatch[1]);
  }

  // Castka
  const amountMatch = text.match(/(?:celkem k úhradě|celkem|total|k úhradě):?\s*([\d\s]+[,.]?\d*)\s*(?:Kč|CZK|EUR)/i);
  if (amountMatch) {
    customFields.amount = amountMatch[1].replace(/\s/g, '');
    const amount = parseFloat(amountMatch[1].replace(/\s/g, '').replace(',', '.'));
    if (amount > 10000) tags.push('velka-faktura');
    else tags.push('mala-faktura');
  }

  // Datum vystaveni
  const dateMatch = text.match(/(?:datum vystavení|date of issue|vystaveno):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (dateMatch) {
    customFields.issue_date = dateMatch[1];
  }

  // Datum splatnosti
  const dueMatch = text.match(/(?:datum splatnosti|due date|splatnost):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (dueMatch) {
    customFields.due_date = dueMatch[1];
  }
}

// PARSOVANI PRO BANKOVNI VYPIS
if (docType === 'vypis') {
  // Cislo uctu
  const accountMatch = text.match(/(?:číslo účtu|account number|č\.ú\.):?\s*([\d\-\/]+)/i);
  if (accountMatch) {
    customFields.account_number = accountMatch[1];
  }

  // Obdobi
  const periodMatch = text.match(/(?:období|period|za období):?\s*([\d\.]+\s*-\s*[\d\.]+)/i);
  if (periodMatch) {
    customFields.period = periodMatch[1];
  }

  // Pocatecni zustatek
  const startMatch = text.match(/(?:počáteční zůstatek|opening balance):?\s*([\d\s]+[,.]?\d*)\s*(?:Kč|CZK)/i);
  if (startMatch) {
    customFields.opening_balance = startMatch[1].replace(/\s/g, '');
  }

  // Konecny zustatek
  const endMatch = text.match(/(?:konečný zůstatek|closing balance|zůstatek):?\s*([\d\s]+[,.]?\d*)\s*(?:Kč|CZK)/i);
  if (endMatch) {
    customFields.closing_balance = endMatch[1].replace(/\s/g, '');
  }

  // Mesic a rok
  const monthMatch = text.match(/(?:leden|únor|březen|duben|květen|červen|červenec|srpen|září|říjen|listopad|prosinec)\s+\d{4}/i);
  if (monthMatch) {
    tags.push(monthMatch[0].toLowerCase().replace(/\s+/g, '-'));
  }
}

// PARSOVANI PRO UCTENKU
if (docType === 'uctenka') {
  // EET kod
  const eetMatch = text.match(/(?:BKP|FIK):?\s*([A-Z0-9\-]+)/i);
  if (eetMatch) {
    customFields.eet_code = eetMatch[1];
  }

  // Castka
  const receiptAmountMatch = text.match(/(?:celkem|total|suma):?\s*([\d\s]+[,.]?\d*)\s*(?:Kč|CZK)/i);
  if (receiptAmountMatch) {
    customFields.amount = receiptAmountMatch[1].replace(/\s/g, '');
  }

  // Datum a cas
  const dateTimeMatch = text.match(/(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})\s+(\d{1,2}:\d{2})/i);
  if (dateTimeMatch) {
    customFields.date = dateTimeMatch[1];
    customFields.time = dateTimeMatch[2];
  }

  // Pokladna
  const cashRegMatch = text.match(/(?:pokladna|kasa|terminal):?\s*([\d\w]+)/i);
  if (cashRegMatch) {
    customFields.cash_register = cashRegMatch[1];
  }
}

// PARSOVANI PRO SMLOUVU
if (docType === 'smlouva') {
  // Cislo smlouvy
  const contractMatch = text.match(/(?:číslo smlouvy|contract number|č\.s\.):?\s*([A-Z0-9\-\/]+)/i);
  if (contractMatch) {
    customFields.contract_number = contractMatch[1];
  }

  // Datum uzavreni
  const signDateMatch = text.match(/(?:uzavřeno dne|signed on|datum uzavření):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (signDateMatch) {
    customFields.sign_date = signDateMatch[1];
  }

  // Platnost od
  const validFromMatch = text.match(/(?:platnost od|valid from|účinnost od):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (validFromMatch) {
    customFields.valid_from = validFromMatch[1];
  }

  // Platnost do
  const validToMatch = text.match(/(?:platnost do|valid to|účinnost do):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (validToMatch) {
    customFields.valid_to = validToMatch[1];
  }
}

// OBECNE TAGY
if (text.match(/2025/)) tags.push('2025');
if (text.match(/2024/)) tags.push('2024');
if (text.match(/2023/)) tags.push('2023');

if (textLower.includes('dph') || textLower.includes('vat')) {
  const vatMatch = text.match(/(\d{1,2})\s*%/i);
  if (vatMatch) {
    tags.push('dph-' + vatMatch[1]);
  }
}

return [{json: {
  status: 'SUCCESS',
  filename: filename,
  filepath: filepath,
  document_type: docType,
  correspondent: correspondent,
  tags: [...new Set(tags)], // unique tags
  custom_fields: customFields,
  text_length: text.length,
  preview: text.substring(0, 500)
}}];

const data = $json;

// Priprava custom fields pro Paperless API
const customFieldsJson = JSON.stringify(data.custom_fields);

return [{json: {
  filepath: data.filepath,
  filename: data.filename,
  title: data.filename.replace('.pdf', ''),
  document_type: data.document_type,
  correspondent: data.correspondent,
  tags: data.tags.join(','),
  custom_fields_json: customFieldsJson,
  // Individual custom fields for Paperless
  invoice_number: data.custom_fields.invoice_number || '',
  variable_symbol: data.custom_fields.variable_symbol || '',
  amount: data.custom_fields.amount || '',
  ico: data.custom_fields.ico || '',
  dic: data.custom_fields.dic || '',
  issue_date: data.custom_fields.issue_date || '',
  due_date: data.custom_fields.due_date || '',
  account_number: data.custom_fields.account_number || '',
  period: data.custom_fields.period || '',
  contract_number: data.custom_fields.contract_number || ''
}}];

const parseData = $node['parseDocument'].json;
const uploadResult = $json;

if (parseData.status === 'ERROR') {
  return [{json: {
    '❌ STATUS': 'EXTRACTION FAILED',
    '📄 File': parseData.filename,
    '⚠️ Error': parseData.message
  }}];
}

if (uploadResult.error) {
  return [{json: {
    '⚠️ STATUS': 'UPLOAD FAILED',
    '📄 File': parseData.filename,
    '📑 Type': parseData.document_type,
    '👤 Correspondent': parseData.correspondent,
    '🏷️ Tags': parseData.tags.join(', '),
    '📝 Custom Fields': JSON.stringify(parseData.custom_fields, null, 2),
    '❌ Upload Error': uploadResult.message || 'Failed to upload to Paperless'
  }}];
}

return [{json: {
  '✅ STATUS': 'COMPLETE SUCCESS',
  '📄 File': parseData.filename,
  '📑 Type': parseData.document_type,
  '👤 Correspondent': parseData.correspondent,
  '🏷️ Tags': parseData.tags.join(', '),
  '📝 Custom Fields': JSON.stringify(parseData.custom_fields, null, 2),
  '🆔 Paperless ID': uploadResult.id || 'Uploaded',
  '📊 Text Length': parseData.text_length + ' characters'
}}];
