const filepath = $node['selectFile'].json.filepath;
const filename = $node['selectFile'].json.filename;

if ($json.error) {
  return [{json: {
    status: 'ERROR',
    message: $json.message || 'PDF.co extraction failed',
    filename: filename
  }}];
}

const text = ($json.body || $json.text || '');
const textLower = text.toLowerCase();

// KLASIFIKACE TYPU DOKUMENTU
let docType = 'unknown';
if (textLower.includes('faktura') || textLower.includes('invoice') || textLower.includes('da≈àov√Ω doklad')) docType = 'faktura';
else if (textLower.includes('√∫ƒçtenka') || textLower.includes('uctenka') || textLower.includes('receipt')) docType = 'uctenka';
else if (textLower.includes('v√Ωpis') || textLower.includes('vypis') || textLower.includes('statement')) docType = 'vypis';
else if (textLower.includes('smlouva') || textLower.includes('contract') || textLower.includes('dohoda')) docType = 'smlouva';
else if (textLower.includes('√∫≈ôedn√≠') || textLower.includes('urednik') || textLower.includes('rozhodnut√≠')) docType = 'urednik';

// NAJIT KORESPONDENTA
let correspondent = 'Unknown';
if (textLower.includes('ƒçsob') || textLower.includes('csob')) correspondent = 'CSOB';
else if (textLower.includes('komerƒçn√≠ banka') || textLower.includes('komercni banka')) correspondent = 'Komercni banka';
else if (textLower.includes('ƒçesk√° spo≈ôitelna') || textLower.includes('ceska sporitelna')) correspondent = 'Ceska sporitelna';
else if (textLower.includes('raiffeisenbank')) correspondent = 'Raiffeisenbank';
else if (textLower.includes('moneta')) correspondent = 'Moneta';
else if (textLower.includes('alza')) correspondent = 'Alza';
else if (textLower.includes('lidl')) correspondent = 'Lidl';
else if (textLower.includes('kaufland')) correspondent = 'Kaufland';
else if (textLower.includes('tesco')) correspondent = 'Tesco';
else if (textLower.includes('amazon')) correspondent = 'Amazon';

// PARSOVANI SPECIFICKYCH UDAJU PODLE TYPU
const customFields = {};
const tags = [docType];

if (correspondent !== 'Unknown') {
  tags.push(correspondent.toLowerCase().replace(/\s+/g, '-'));
}

// PARSOVANI PRO FAKTURU
if (docType === 'faktura') {
  // ICO
  const icoMatch = text.match(/IƒåO?:?\s*(\d{8})/i);
  if (icoMatch) {
    customFields.ico = icoMatch[1];
    tags.push('ico-' + icoMatch[1]);
  }

  // DIC
  const dicMatch = text.match(/DIƒå:?\s*(CZ\d{8,10})/i);
  if (dicMatch) {
    customFields.dic = dicMatch[1];
    tags.push('dic-' + dicMatch[1].toLowerCase());
  }

  // Cislo faktury
  const invoiceMatch = text.match(/(?:ƒç√≠slo faktury|invoice number|faktura ƒç\.?|evidenƒçn√≠ ƒç√≠slo):?\s*([A-Z0-9\-\/]+)/i);
  if (invoiceMatch) {
    customFields.invoice_number = invoiceMatch[1];
  }

  // Variabilni symbol
  const vsMatch = text.match(/(?:VS|variabiln√≠ symbol|var\.\s*symbol):?\s*(\d+)/i);
  if (vsMatch) {
    customFields.variable_symbol = vsMatch[1];
    tags.push('vs-' + vsMatch[1]);
  }

  // Castka
  const amountMatch = text.match(/(?:celkem k √∫hradƒõ|celkem|total|k √∫hradƒõ|ƒç√°stka celkem):?\s*([\d\s]+[,.]?\d*)\s*(?:Kƒç|CZK|EUR)/i);
  if (amountMatch) {
    customFields.amount = amountMatch[1].replace(/\s/g, '');
    const amount = parseFloat(amountMatch[1].replace(/\s/g, '').replace(',', '.'));
    if (amount > 10000) tags.push('velka-faktura');
    else tags.push('mala-faktura');
  }

  // Datum vystaveni
  const dateMatch = text.match(/(?:datum vystaven√≠|date of issue|vystaveno|datum vyd√°n√≠):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (dateMatch) {
    customFields.issue_date = dateMatch[1];
  }

  // Datum splatnosti
  const dueMatch = text.match(/(?:datum splatnosti|due date|splatnost|splatno):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (dueMatch) {
    customFields.due_date = dueMatch[1];
  }
}

// PARSOVANI PRO BANKOVNI VYPIS
if (docType === 'vypis') {
  // Cislo uctu
  const accountMatch = text.match(/(?:ƒç√≠slo √∫ƒçtu|account number|ƒç\.√∫\.|ƒç√≠slo va≈°eho √∫ƒçtu):?\s*([\d\-\/]+)/i);
  if (accountMatch) {
    customFields.account_number = accountMatch[1];
  }

  // Obdobi
  const periodMatch = text.match(/(?:obdob√≠|period|za obdob√≠|v√Ωpis za):?\s*([\d\.]+\s*-\s*[\d\.]+)/i);
  if (periodMatch) {
    customFields.period = periodMatch[1];
  }

  // Pocatecni zustatek
  const startMatch = text.match(/(?:poƒç√°teƒçn√≠ z≈Østatek|opening balance|z≈Østatek k):?\s*([\d\s]+[,.]?\d*)\s*(?:Kƒç|CZK)/i);
  if (startMatch) {
    customFields.opening_balance = startMatch[1].replace(/\s/g, '');
  }

  // Konecny zustatek
  const endMatch = text.match(/(?:koneƒçn√Ω z≈Østatek|closing balance|nov√Ω z≈Østatek|z≈Østatek ke dni):?\s*([\d\s]+[,.]?\d*)\s*(?:Kƒç|CZK)/i);
  if (endMatch) {
    customFields.closing_balance = endMatch[1].replace(/\s/g, '');
  }

  // Mesic a rok
  const monthMatch = text.match(/(?:leden|√∫nor|b≈ôezen|duben|kvƒõten|ƒçerven|ƒçervenec|srpen|z√°≈ô√≠|≈ô√≠jen|listopad|prosinec)\s+\d{4}/i);
  if (monthMatch) {
    tags.push(monthMatch[0].toLowerCase().replace(/\s+/g, '-'));
  }
}

// PARSOVANI PRO UCTENKU
if (docType === 'uctenka') {
  // EET kod
  const eetMatch = text.match(/(?:BKP|FIK|PKP):?\s*([A-Z0-9\-]+)/i);
  if (eetMatch) {
    customFields.eet_code = eetMatch[1];
  }

  // Castka
  const receiptAmountMatch = text.match(/(?:celkem|total|suma|k platbƒõ):?\s*([\d\s]+[,.]?\d*)\s*(?:Kƒç|CZK)/i);
  if (receiptAmountMatch) {
    customFields.amount = receiptAmountMatch[1].replace(/\s/g, '');
  }

  // Datum a cas
  const dateTimeMatch = text.match(/(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})\s+(\d{1,2}:\d{2})/i);
  if (dateTimeMatch) {
    customFields.date = dateTimeMatch[1];
    customFields.time = dateTimeMatch[2];
  }

  // Pokladna
  const cashRegMatch = text.match(/(?:pokladna|kasa|terminal|pos):?\s*([\d\w]+)/i);
  if (cashRegMatch) {
    customFields.cash_register = cashRegMatch[1];
  }
}

// PARSOVANI PRO SMLOUVU
if (docType === 'smlouva') {
  // Cislo smlouvy
  const contractMatch = text.match(/(?:ƒç√≠slo smlouvy|contract number|ƒç\.s\.|smlouva ƒç\.):?\s*([A-Z0-9\-\/]+)/i);
  if (contractMatch) {
    customFields.contract_number = contractMatch[1];
  }

  // Datum uzavreni
  const signDateMatch = text.match(/(?:uzav≈ôeno dne|signed on|datum uzav≈ôen√≠|podeps√°no):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (signDateMatch) {
    customFields.sign_date = signDateMatch[1];
  }

  // Platnost od
  const validFromMatch = text.match(/(?:platnost od|valid from|√∫ƒçinnost od):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (validFromMatch) {
    customFields.valid_from = validFromMatch[1];
  }

  // Platnost do
  const validToMatch = text.match(/(?:platnost do|valid to|√∫ƒçinnost do|plat√≠ do):?\s*(\d{1,2}[\.\-]\d{1,2}[\.\-]\d{4})/i);
  if (validToMatch) {
    customFields.valid_to = validToMatch[1];
  }
}

// OBECNE TAGY
if (text.match(/2025/)) tags.push('2025');
if (text.match(/2024/)) tags.push('2024');
if (text.match(/2023/)) tags.push('2023');

if (textLower.includes('dph') || textLower.includes('vat')) {
  const vatMatch = text.match(/(\d{1,2})\s*%/i);
  if (vatMatch) {
    tags.push('dph-' + vatMatch[1]);
  }
}

return [{json: {
  status: 'SUCCESS',
  filename: filename,
  filepath: filepath,
  document_type: docType,
  correspondent: correspondent,
  tags: [...new Set(tags)],
  custom_fields: customFields,
  text_length: text.length,
  preview: text.substring(0, 500)
}}];

