// STAGE 1: FILENAME ANALYSIS
const filename = $binary.data.fileName.toLowerCase();
let filenameScore = 0;
let predictedType = 'unknown';
let company = 'unknown';

// BodovÃ¡nÃ­ podle nÃ¡zvu (+30 bodÅ¯)
if (filename.includes('faktura') || filename.includes('invoice')) {
  filenameScore += 30;
  predictedType = 'invoice';
} else if (filename.includes('vypis') || filename.includes('statement')) {
  filenameScore += 30;
  predictedType = 'bank_statement';
} else if (filename.includes('smlouva') || filename.includes('contract')) {
  filenameScore += 30;
  predictedType = 'contract';
}

// Detekce spoleÄnosti
if (filename.includes('mbw')) {
  company = 'Media Bohemia World';
} else if (filename.includes('csob') || filename.includes('Äsob')) {
  company = 'ÄŒSOB';
} else if (filename.includes('alza')) {
  company = 'Alza';
}

// PÅ™edej data dÃ¡l
return {
  json: {
    filename: $binary.data.fileName,
    filenameScore: filenameScore,
    predictedType: predictedType,
    company: company,
    filePath: $binary.data.filePath
  },
  binary: $binary
};

// STAGE 3: PATTERN MATCHING & CZECH INVOICE DETECTION
const text = $json.text || '';
const textLower = text.toLowerCase();
let totalScore = $json.filenameScore || 0;
let scoreMap = {};
let documentType = 'unknown';
let category = 'unclear';
let czechInvoiceData = {};

// ÄŒeskÃ© faktury - povinnÃ© nÃ¡leÅ¾itosti (min 80 bodÅ¯)
if (text.match(/faktura|daÅˆovÃ½\s+doklad|tax\s+document/i)) {
  scoreMap.invoice_label = 30;
  totalScore += 30;
}

const icoMatch = text.match(/IÄŒO?\s*:?\s*(\d{8})/i);
if (icoMatch) {
  scoreMap.ico = 25;
  totalScore += 25;
  czechInvoiceData.ico = icoMatch[1];
}

const dicMatch = text.match(/DIÄŒ\s*:?\s*(CZ\d{8,10})/i);
if (dicMatch) {
  scoreMap.dic = 25;
  totalScore += 25;
  czechInvoiceData.dic = dicMatch[1];
}

if (text.match(/datum\s+vystavenÃ­|date\s+of\s+issue|vystaveno/i)) {
  scoreMap.issue_date = 15;
  totalScore += 15;
}

if (text.match(/DUZP|datum\s+uskuteÄnÄ›nÃ­|zdanitelnÃ©\s+plnÄ›nÃ­/i)) {
  scoreMap.duzp = 15;
  totalScore += 15;
}

if (text.match(/ÄÃ­slo\s+faktury|invoice\s+number|Ä\.\s*faktury/i)) {
  scoreMap.invoice_number = 20;
  totalScore += 20;
}

if (text.match(/dodavatel|supplier|prodÃ¡vajÃ­cÃ­/i)) {
  scoreMap.supplier = 15;
  totalScore += 15;
}

if (text.match(/DPH|VAT|daÅˆ\s+z\s+pÅ™idanÃ©/i)) {
  scoreMap.vat = 20;
  totalScore += 20;
}

const amountMatch = text.match(/celkem:?\s*([\d\s]+[,.]?\d*)\s*(?:KÄ|CZK)/i);
if (amountMatch) {
  scoreMap.total_amount = 15;
  totalScore += 15;
  czechInvoiceData.amount = amountMatch[1];
}

// RozhodnutÃ­ o ÄeskÃ© faktuÅ™e
if (totalScore >= 80) {
  documentType = 'invoice';
  category = 'invoices';
  czechInvoiceData.is_czech_invoice = true;
  czechInvoiceData.score = totalScore;
  czechInvoiceData.score_map = scoreMap;
}
// BankovnÃ­ vÃ½pisy (min 50 bodÅ¯)
else {
  let bankScore = 0;
  if (textLower.includes('vÃ½pis') || textLower.includes('statement')) bankScore += 25;
  if (textLower.includes('zÅ¯statek') || textLower.includes('balance')) bankScore += 25;
  if (text.match(/IBAN|CZ\d{22}/)) bankScore += 20;

  if (bankScore >= 50) {
    documentType = 'bank_statement';
    category = 'banking';
    totalScore += bankScore;
  }
  // Smlouvy
  else {
    let contractScore = 0;
    if (textLower.includes('smlouva') || textLower.includes('contract')) contractScore += 30;
    if (textLower.includes('smluvnÃ­ strany')) contractScore += 25;

    if (contractScore >= 50) {
      documentType = 'contract';
      category = 'contracts';
      totalScore += contractScore;
    }
  }
}

// Confidence vÃ½poÄet
const confidence = Math.min(95, 50 + Math.floor(totalScore / 4));

return {
  json: {
    ...($json),
    text: text,
    documentType: documentType,
    category: category,
    totalScore: totalScore,
    confidence: confidence,
    scoreMap: scoreMap,
    czechInvoiceData: czechInvoiceData
  }
};

// STAGE 5: CORRESPONDENT DETECTION
const text = ($json.text || '').toLowerCase();
const filename = ($json.filename || '').toLowerCase();
let correspondent = null;

// Domain mapping
const domainMap = {
  'csob.cz': 'ÄŒSOB',
  'kb.cz': 'KomerÄnÃ­ banka',
  'csas.cz': 'ÄŒeskÃ¡ spoÅ™itelna',
  'rb.cz': 'Raiffeisenbank',
  'moneta.cz': 'Moneta',
  'alza.cz': 'Alza',
  'amazon.com': 'Amazon',
  'mbw.cz': 'Media Bohemia World',
  'media-bohemia.cz': 'Media Bohemia World'
};

// Kontrola domÃ©n v textu
for (const [domain, name] of Object.entries(domainMap)) {
  if (text.includes(domain) || filename.includes(domain.split('.')[0])) {
    correspondent = name;
    break;
  }
}

// SpecifickÃ© detekce
if (!correspondent) {
  if (filename.includes('mbw') || text.includes('media bohemia')) {
    correspondent = 'Media Bohemia World';
  } else if (filename.includes('alza') || text.includes('alza')) {
    correspondent = 'Alza';
  } else if (filename.includes('csob') || filename.includes('Äsob')) {
    correspondent = 'ÄŒSOB';
  }
}

// IÄŒO databÃ¡ze
if (!correspondent && $json.czechInvoiceData && $json.czechInvoiceData.ico) {
  const icoDatabase = {
    '26178370': 'Media Bohemia World',
    '27082440': 'Alza.cz',
    '00001350': 'ÄŒSOB'
  };
  correspondent = icoDatabase[$json.czechInvoiceData.ico] || null;
}

return {
  json: {
    ...($json),
    correspondent: correspondent || $json.company || 'unknown'
  }
};

// STAGE 6: TAG GENERATION podle CLAUDE.md
const tags = [];
const now = new Date();

// CLAUDE.md kategorie mapping
const CATEGORIES = {
  'banking': 'paperless-ngx-bankovnictvi',
  'invoices': 'paperless-ngx-faktury',
  'office': 'paperless-ngx-office',
  'contracts': 'paperless-ngx-smlouvy',
  'official': 'paperless-ngx-uredni-sdeleni',
  'medical': 'paperless-ngx-lekarske-zpravy',
  'subscriptions': 'paperless-ngx-predplatna',
  'bills_tv': 'paperless-ngx-platby-za-tv',
  'bills_internet': 'paperless-ngx-platby-za-internet',
  'bills_services': 'paperless-ngx-platby-za-sluzby',
  'commercial': 'paperless-ngx-obchodni-nabidky',
  'ecommerce': 'paperless-ngx-eshop',
  'social_media': 'paperless-ngx-socialni-site',
  'system_notifications': 'paperless-ngx-systemove-notifikace',
  'unclear': 'paperless-ngx-nejasne',
  'spam': 'paperless-ngx-spam'
};

// 1. Kategorie tag (povinnÃ½)
const category = $json.category || 'unclear';
tags.push(CATEGORIES[category] || CATEGORIES.unclear);

// 2. Correspondent tag
if ($json.correspondent && $json.correspondent !== 'unknown') {
  tags.push(`correspondent:${$json.correspondent}`);
}

// 3. Typ dokumentu
tags.push(`type:${$json.documentType}`);
if ($json.czechInvoiceData && $json.czechInvoiceData.is_czech_invoice) {
  tags.push('subtype:czech_invoice');
}

// 4. Metadata z ÄeskÃ© faktury
if ($json.czechInvoiceData) {
  if ($json.czechInvoiceData.ico) {
    tags.push(`ico:${$json.czechInvoiceData.ico}`);
  }
  if ($json.czechInvoiceData.dic) {
    tags.push(`dic:${$json.czechInvoiceData.dic}`);
  }
  if ($json.czechInvoiceData.amount) {
    const amount = parseFloat($json.czechInvoiceData.amount.replace(',', '.').replace(/\s/g, ''));
    if (amount > 10000) {
      tags.push('velka-faktura');
    } else {
      tags.push('mala-faktura');
    }
  }
}

// 5. VariabilnÃ­ symbol
const vsMatch = ($json.text || '').match(/VS:?\s*(\d+)/);
if (vsMatch) {
  tags.push(`vs:${vsMatch[1]}`);
}

// 6. Rok a mÄ›sÃ­c
const year = now.getFullYear();
const months = [
  'leden', 'unor', 'brezen', 'duben',
  'kveten', 'cerven', 'cervenec', 'srpen',
  'zari', 'rijen', 'listopad', 'prosinec'
];

// Detekce roku
for (let y = year; y >= year - 2; y--) {
  if ($json.text && $json.text.includes(y.toString())) {
    tags.push(y.toString());
    break;
  }
}

// AktuÃ¡lnÃ­ mÄ›sÃ­c
tags.push(months[now.getMonth()]);

// 7. Confidence
tags.push(`konfidence-${$json.confidence}`);

// 8. SkÃ³re
if ($json.totalScore) {
  tags.push(`score-${$json.totalScore}`);
}

// 9. Zdroj
tags.push('import-mbw-batch');
tags.push('n8n-workflow');

// OdstraÅˆ duplicity
const uniqueTags = [...new Set(tags)];

return {
  json: {
    ...($json),
    tags: uniqueTags,
    title: `${$json.correspondent || ''} - ${$json.documentType} - ${$json.filename}`.substring(0, 128)
  }
};

// Create missing tags
const existingTags = $node["ğŸ“¥ Get Existing Tags"].json.results || [];
const existingTagNames = existingTags.map(t => t.name);
const requiredTags = $json.tags || [];
const tagsToCreate = [];

// Barvy podle typu tagu
const getTagColor = (tagName) => {
  if (tagName.startsWith('paperless-ngx-')) return '#0066CC';
  if (tagName.startsWith('correspondent:')) return '#009900';
  if (tagName.startsWith('type:')) return '#CC6600';
  if (tagName.startsWith('ico:') || tagName.startsWith('dic:')) return '#9900CC';
  if (tagName.startsWith('vs:')) return '#CC0099';
  if (tagName.includes('faktura')) return '#FF0000';
  if (['leden', 'unor', 'brezen', 'duben', 'kveten', 'cerven',
       'cervenec', 'srpen', 'zari', 'rijen', 'listopad', 'prosinec'].includes(tagName)) {
    return '#00CCCC';
  }
  return '#808080';
};

// Najdi tagy kterÃ© je potÅ™eba vytvoÅ™it
for (const tagName of requiredTags) {
  if (!existingTagNames.includes(tagName)) {
    tagsToCreate.push({
      name: tagName,
      color: getTagColor(tagName),
      is_inbox_tag: false,
      matching_algorithm: 1,
      match: tagName,
      is_insensitive: true
    });
  }
}

return tagsToCreate.map(tag => ({json: tag}));

// Prepare final upload data
const allTags = $node["ğŸ“¥ Get Existing Tags"].json.results || [];
const createdTags = $node["â• Create Tag"].json || [];

// Merge all tags
const tagMap = {};
for (const tag of allTags) {
  tagMap[tag.name] = tag.id;
}
for (const tag of createdTags) {
  if (tag.id) tagMap[tag.name] = tag.id;
}

// Get tag IDs for our document
const tagIds = [];
for (const tagName of $json.tags) {
  if (tagMap[tagName]) {
    tagIds.push(tagMap[tagName]);
  }
}

return {
  json: {
    title: $json.title,
    tags: tagIds,
    filename: $json.filename,
    filePath: $json.filePath
  },
  binary: $binary
};

// Log success or failure
if ($json.statusCode === 200 || $json.statusCode === 201) {
  return {
    json: {
      status: 'success',
      document_id: $json.body,
      title: $node["ğŸ“¦ Prepare Upload"].json.title,
      tags_count: $node["ğŸ“¦ Prepare Upload"].json.tags.length,
      filename: $node["ğŸ“¦ Prepare Upload"].json.filename
    }
  };
} else {
  return {
    json: {
      status: 'failed',
      error: $json.body,
      filename: $node["ğŸ“¦ Prepare Upload"].json.filename
    }
  };
}
