// Kompletn√≠ business document parser
const text = ($json.body || $json.text || '');
const textLower = text.toLowerCase();
const filename = $node['setFile'].json.filepath.split('/').pop();
const filepath = $node['setFile'].json.filepath;

// === DETEKCE TYPU DOKUMENTU ===
let docType = 'unknown';
let confidence = 0;

// Bodov√°n√≠ pro ka≈æd√Ω typ
const scores = {
  faktura: 0,
  vypis: 0,
  smlouva: 0,
  objednavka: 0,
  uctenka: 0,
  dodaci_list: 0,
  mzdovy_list: 0
};

// Faktury
if (textLower.includes('faktura') || textLower.includes('invoice')) scores.faktura += 30;
if (textLower.includes('da≈àov√Ω doklad')) scores.faktura += 30;
if (textLower.includes('vy√∫ƒçtov√°n√≠') || textLower.includes('vyuctovani')) scores.faktura += 25;
if (text.match(/IƒåO?:?\s*\d{8}/i)) scores.faktura += 15;
if (text.match(/DIƒå:?\s*CZ\d{8,10}/i)) scores.faktura += 15;
if (text.match(/variabiln√≠ symbol/i)) scores.faktura += 10;

// Bankovn√≠ v√Ωpisy
if (textLower.includes('v√Ωpis') || textLower.includes('vypis')) scores.vypis += 30;
if (textLower.includes('z≈Østatek') || textLower.includes('zustatek')) scores.vypis += 20;
if (textLower.includes('transakce')) scores.vypis += 15;
if (text.match(/\d{2,6}[-/]?\d{4,10}\/\d{4}/)) scores.vypis += 10;

// Smlouvy
if (textLower.includes('smlouva')) scores.smlouva += 30;
if (textLower.includes('smluvn√≠ strany')) scores.smlouva += 25;
if (textLower.includes('p≈ôedmƒõt smlouvy')) scores.smlouva += 20;
if (textLower.includes('platnost od')) scores.smlouva += 15;

// Objedn√°vky
if (textLower.includes('objedn√°vka') || textLower.includes('order')) scores.objednavka += 30;
if (textLower.includes('term√≠n dod√°n√≠')) scores.objednavka += 20;
if (textLower.includes('dodac√≠ adresa')) scores.objednavka += 15;

// √öƒçtenky
if (textLower.includes('√∫ƒçtenka') || textLower.includes('uctenka')) scores.uctenka += 30;
if (textLower.includes('pokladn√≠ doklad')) scores.uctenka += 25;
if (text.match(/(?:BKP|FIK|PKP):?\s*[A-Z0-9-]+/i)) scores.uctenka += 30;
if (textLower.includes('eet')) scores.uctenka += 20;

// Dodac√≠ listy
if (textLower.includes('dodac√≠ list')) scores.dodaci_list += 30;
if (textLower.includes('p≈ôevzal')) scores.dodaci_list += 20;
if (textLower.includes('balen√≠')) scores.dodaci_list += 15;

// Mzdov√© listy
if (textLower.includes('mzdov√Ω list')) scores.mzdovy_list += 30;
if (textLower.includes('hrub√° mzda')) scores.mzdovy_list += 25;
if (textLower.includes('ƒçist√° mzda')) scores.mzdovy_list += 25;

// Urƒçen√≠ typu podle nejvy≈°≈°√≠ho sk√≥re
let maxScore = 0;
for (const [type, score] of Object.entries(scores)) {
  if (score > maxScore) {
    maxScore = score;
    docType = type;
    confidence = Math.min(100, score);
  }
}

// === UNIVERZ√ÅLN√ç EXTRAKCE ===
const customFields = {};
const tags = [];

// Rok a mƒõs√≠c
const yearMatch = text.match(/20(2[3-9])/g);
if (yearMatch && yearMatch.length > 0) {
  const year = '20' + yearMatch[0].substring(2);
  customFields.rok = year;
  tags.push(year);
}

const months = {
  'leden': '01', 'ledna': '01', '√∫nor': '02', '√∫nora': '02',
  'b≈ôezen': '03', 'b≈ôezna': '03', 'duben': '04', 'dubna': '04',
  'kvƒõten': '05', 'kvƒõtna': '05', 'ƒçerven': '06', 'ƒçervna': '06',
  'ƒçervenec': '07', 'ƒçervence': '07', 'srpen': '08', 'srpna': '08',
  'z√°≈ô√≠': '09', '≈ô√≠jen': '10', '≈ô√≠jna': '10',
  'listopad': '11', 'listopadu': '11', 'prosinec': '12', 'prosince': '12'
};
for (const [month, num] of Object.entries(months)) {
  if (textLower.includes(month)) {
    tags.push('mesic-' + num);
    customFields.mesic = month;
    break;
  }
}

// === SPECIFICK√â PARSOV√ÅN√ç PODLE TYPU ===

if (docType === 'faktura') {
  // IDENTIFIKACE
  const invoicePatterns = [
    /(?:faktura|invoice|doklad)\s*ƒç\.?\s*:?\s*([A-Z0-9\-\/]+)/i,
    /(?:ƒç√≠slo faktury|invoice number)\s*:?\s*([A-Z0-9\-\/]+)/i,
    /(?:evidenƒçn√≠ ƒç√≠slo)\s*:?\s*([A-Z0-9\-\/]+)/i,
    /vy√∫ƒçtov√°n√≠.*?ƒç\.?\s*(\d+)/i
  ];
  for (const pattern of invoicePatterns) {
    const match = text.match(pattern);
    if (match) {
      customFields.cislo_faktury = match[1];
      tags.push('faktura-' + match[1]);
      break;
    }
  }

  // DODAVATEL
  const dodavatelPatterns = [
    /dodavatel:?\s*([^\n]+)/i,
    /vystavitel:?\s*([^\n]+)/i,
    /prod√°vaj√≠c√≠:?\s*([^\n]+)/i
  ];
  for (const pattern of dodavatelPatterns) {
    const match = text.match(pattern);
    if (match) {
      customFields.dodavatel_nazev = match[1].trim();
      tags.push('dodavatel-' + match[1].trim().toLowerCase().replace(/[\s.]/g, '-'));
      break;
    }
  }

  // IƒåO a DIƒå dodavatele
  const icoMatch = text.match(/IƒåO?:?\s*(\d{8})/i);
  if (icoMatch) {
    customFields.dodavatel_ico = icoMatch[1];
    tags.push('ico-' + icoMatch[1]);
  }

  const dicMatch = text.match(/DIƒå:?\s*(CZ\d{8,10})/i);
  if (dicMatch) {
    customFields.dodavatel_dic = dicMatch[1];
    tags.push('dic-' + dicMatch[1].toLowerCase());
  }

  // ODBƒöRATEL
  const odberatelPatterns = [
    /odbƒõratel:?\s*([^\n]+(?:\n[^\n]+)?)/i,
    /z√°kazn√≠k:?\s*([^\n]+)/i,
    /kupuj√≠c√≠:?\s*([^\n]+)/i
  ];
  for (const pattern of odberatelPatterns) {
    const match = text.match(pattern);
    if (match) {
      customFields.odberatel_nazev = match[1].trim().replace(/\n/g, ', ');
      break;
    }
  }

  // FINANƒåN√ç √öDAJE
  // Celkov√° ƒç√°stka
  const amountPatterns = [
    /celkem\s+s\s+dph:?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i,
    /celkem\s+k\s+√∫hradƒõ:?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i,
    /celkem:?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i,
    /total:?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i
  ];
  for (const pattern of amountPatterns) {
    const match = text.match(pattern);
    if (match) {
      customFields.castka_celkem_s_dph = match[1].replace(/\s/g, '');
      const amount = parseFloat(match[1].replace(/\s/g, '').replace(',', '.'));
      if (amount > 50000) tags.push('vysoka-castka');
      else if (amount > 10000) tags.push('velka-faktura');
      else if (amount > 1000) tags.push('stredni-faktura');
      else tags.push('mala-faktura');
      break;
    }
  }

  // ƒå√°stka bez DPH
  const bezDphMatch = text.match(/(?:bez dph|z√°klad danƒõ):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk)/i);
  if (bezDphMatch) {
    customFields.castka_bez_dph = bezDphMatch[1].replace(/\s/g, '');
  }

  // DPH
  const dphMatch = text.match(/(?:dph|da≈à)\s+(?:21\s*%)?:?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk)/i);
  if (dphMatch) {
    customFields.dph_21_castka = dphMatch[1].replace(/\s/g, '');
  }

  // Sazba DPH
  const sazbaDphMatch = text.match(/(\d{1,2})\s*%/g);
  if (sazbaDphMatch) {
    const sazby = [...new Set(sazbaDphMatch.map(s => s.replace(/\s*%/, '')))];
    customFields.dph_sazby = sazby.join(',');
    sazby.forEach(s => tags.push('dph-' + s));
  }

  // PLATEBN√ç √öDAJE
  const vsMatch = text.match(/(?:VS|variabiln√≠ symbol|var\.\s*symbol):?\s*(\d+)/i);
  if (vsMatch) {
    customFields.variabilni_symbol = vsMatch[1];
    tags.push('vs-' + vsMatch[1]);
  }

  const ksMatch = text.match(/(?:KS|konstantn√≠ symbol):?\s*(\d+)/i);
  if (ksMatch) {
    customFields.konstantni_symbol = ksMatch[1];
  }

  const ssMatch = text.match(/(?:SS|specifick√Ω symbol):?\s*(\d+)/i);
  if (ssMatch) {
    customFields.specificky_symbol = ssMatch[1];
  }

  // ƒå√≠slo √∫ƒçtu
  const ucetMatch = text.match(/(\d{2,6}[-/]?\d{4,10}\/\d{4})/i);
  if (ucetMatch) {
    customFields.cislo_uctu = ucetMatch[1];
  }

  // IBAN
  const ibanMatch = text.match(/([A-Z]{2}\d{2}[A-Z0-9]{4,30})/i);
  if (ibanMatch) {
    customFields.iban = ibanMatch[1];
  }

  // DATUMY
  const datePatterns = [
    /(?:datum vystaven√≠|date of issue|vystaveno):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i,
    /(?:datum zdaniteln√©ho plnƒõn√≠|duzp):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i
  ];
  for (const pattern of datePatterns) {
    const match = text.match(pattern);
    if (match) {
      customFields.datum_vystaveni = match[1];
      break;
    }
  }

  const dueMatch = text.match(/(?:datum splatnosti|due date|splatnost):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (dueMatch) {
    customFields.datum_splatnosti = dueMatch[1];
    // Kontrola splatnosti
    const dueDate = new Date(dueMatch[1].split(/[.\-]/).reverse().join('-'));
    const today = new Date();
    const diffDays = Math.ceil((dueDate - today) / (1000 * 60 * 60 * 24));
    if (diffDays < 0) tags.push('po-splatnosti');
    else if (diffDays <= 7) tags.push('blizi-se-splatnost');
  }

  // Mƒõna
  let mena = 'CZK';
  if (textLower.includes('eur')) mena = 'EUR';
  else if (textLower.includes('usd')) mena = 'USD';
  customFields.mena = mena;
  tags.push('mena-' + mena.toLowerCase());

  // Zp≈Øsob platby
  if (textLower.includes('p≈ôevodem') || textLower.includes('bankovn√≠ p≈ôevod')) {
    customFields.zpusob_platby = 'Bankovn√≠ p≈ôevod';
  } else if (textLower.includes('hotovƒõ') || textLower.includes('hotovost')) {
    customFields.zpusob_platby = 'Hotovƒõ';
  } else if (textLower.includes('kartou')) {
    customFields.zpusob_platby = 'Platebn√≠ karta';
  }
}

else if (docType === 'vypis') {
  // IDENTIFIKACE √öƒåTU
  const accountMatch = text.match(/(?:ƒç√≠slo √∫ƒçtu|account number|ƒç\.√∫\.):?\s*([\d\-\/]+)/i);
  if (accountMatch) {
    customFields.cislo_uctu = accountMatch[1];
    tags.push('ucet-' + accountMatch[1].replace(/[\-\/]/g, ''));
  }

  // Banka
  const banks = [
    {name: 'ƒåSOB', pattern: /ƒçsob|ƒçeskoslovensk√° obchodn√≠ banka/i},
    {name: 'ƒåesk√° spo≈ôitelna', pattern: /ƒçesk√° spo≈ôitelna|ceska sporitelna/i},
    {name: 'Komerƒçn√≠ banka', pattern: /komerƒçn√≠ banka|komercni banka|kb/i},
    {name: 'Raiffeisenbank', pattern: /raiffeisenbank|raiffeisen/i},
    {name: 'UniCredit Bank', pattern: /unicredit/i},
    {name: 'Moneta', pattern: /moneta|money bank/i},
    {name: 'Fio banka', pattern: /fio/i},
    {name: 'Air Bank', pattern: /air bank|airbank/i},
    {name: 'mBank', pattern: /mbank/i}
  ];

  for (const bank of banks) {
    if (bank.pattern.test(text)) {
      customFields.nazev_banky = bank.name;
      tags.push('banka-' + bank.name.toLowerCase().replace(/\s+/g, '-'));
      break;
    }
  }

  // Obdob√≠
  const periodMatch = text.match(/(?:obdob√≠|period|za obdob√≠):?\s*([\d.]+\s*-\s*[\d.]+)/i);
  if (periodMatch) {
    customFields.obdobi = periodMatch[1];
  }

  // ƒå√≠slo v√Ωpisu
  const vypisMatch = text.match(/(?:v√Ωpis ƒç\.|ƒç√≠slo v√Ωpisu):?\s*(\d+)/i);
  if (vypisMatch) {
    customFields.cislo_vypisu = vypisMatch[1];
  }

  // Z≈ÆSTATKY
  const startMatch = text.match(/(?:poƒç√°teƒçn√≠ z≈Østatek|opening balance):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk)/i);
  if (startMatch) {
    customFields.pocatecni_zustatek = startMatch[1].replace(/\s/g, '');
  }

  const endMatch = text.match(/(?:koneƒçn√Ω z≈Østatek|closing balance|nov√Ω z≈Østatek):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk)/i);
  if (endMatch) {
    customFields.konecny_zustatek = endMatch[1].replace(/\s/g, '');
  }

  const availMatch = text.match(/(?:disponibiln√≠ z≈Østatek|available balance):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk)/i);
  if (availMatch) {
    customFields.disponibilni_zustatek = availMatch[1].replace(/\s/g, '');
  }

  // Poƒçet transakc√≠
  const transMatch = text.match(/(?:poƒçet transakc√≠|poƒçet operac√≠):?\s*(\d+)/i);
  if (transMatch) {
    customFields.pocet_transakci = transMatch[1];
  }
}

else if (docType === 'smlouva') {
  // IDENTIFIKACE
  const contractMatch = text.match(/(?:smlouva ƒç\.|ƒç√≠slo smlouvy|contract number):?\s*([A-Z0-9\-\/]+)/i);
  if (contractMatch) {
    customFields.cislo_smlouvy = contractMatch[1];
    tags.push('smlouva-' + contractMatch[1]);
  }

  // Typ smlouvy
  if (textLower.includes('kupn√≠ smlouva')) {
    customFields.typ_smlouvy = 'Kupn√≠';
    tags.push('kupni-smlouva');
  } else if (textLower.includes('n√°jemn√≠ smlouva')) {
    customFields.typ_smlouvy = 'N√°jemn√≠';
    tags.push('najemni-smlouva');
  } else if (textLower.includes('servisn√≠ smlouva')) {
    customFields.typ_smlouvy = 'Servisn√≠';
    tags.push('servisni-smlouva');
  } else if (textLower.includes('pracovn√≠ smlouva')) {
    customFields.typ_smlouvy = 'Pracovn√≠';
    tags.push('pracovni-smlouva');
  }

  // Smluvn√≠ strany
  const stranyMatch = text.match(/(?:smluvn√≠ strany|mezi):?\s*([^\n]+(?:\n[^\n]+)?)/i);
  if (stranyMatch) {
    customFields.smluvni_strany = stranyMatch[1].trim();
  }

  // Datum uzav≈ôen√≠
  const signDateMatch = text.match(/(?:uzav≈ôeno dne|signed on|datum uzav≈ôen√≠):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (signDateMatch) {
    customFields.datum_uzavreni = signDateMatch[1];
  }

  // Platnost
  const validFromMatch = text.match(/(?:platnost od|valid from|√∫ƒçinnost od):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (validFromMatch) {
    customFields.platnost_od = validFromMatch[1];
  }

  const validToMatch = text.match(/(?:platnost do|valid to|√∫ƒçinnost do):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (validToMatch) {
    customFields.platnost_do = validToMatch[1];
  }

  // V√Ωpovƒõdn√≠ lh≈Øta
  const vypovMatch = text.match(/(?:v√Ωpovƒõdn√≠ lh≈Øta|notice period):?\s*([^\n]+)/i);
  if (vypovMatch) {
    customFields.vypovedni_lhuta = vypovMatch[1].trim();
  }

  // P≈ôedmƒõt smlouvy
  const subjectMatch = text.match(/(?:p≈ôedmƒõt smlouvy|subject):?\s*([^\n]+)/i);
  if (subjectMatch) {
    customFields.predmet_smlouvy = subjectMatch[1].trim();
  }

  // Hodnota smlouvy
  const valueMatch = text.match(/(?:hodnota smlouvy|celkov√° hodnota|contract value):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i);
  if (valueMatch) {
    customFields.celkova_hodnota = valueMatch[1].replace(/\s/g, '');
  }
}

else if (docType === 'objednavka') {
  // IDENTIFIKACE
  const orderMatch = text.match(/(?:objedn√°vka ƒç\.|ƒç√≠slo objedn√°vky|order number):?\s*([A-Z0-9\-\/]+)/i);
  if (orderMatch) {
    customFields.cislo_objednavky = orderMatch[1];
    tags.push('objednavka-' + orderMatch[1]);
  }

  // Datum objedn√°vky
  const orderDateMatch = text.match(/(?:datum objedn√°vky|order date|objedn√°no):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (orderDateMatch) {
    customFields.datum_objednavky = orderDateMatch[1];
  }

  // Term√≠n dod√°n√≠
  const deliveryMatch = text.match(/(?:term√≠n dod√°n√≠|delivery date|doruƒçen√≠):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (deliveryMatch) {
    customFields.termin_dodani = deliveryMatch[1];
  }

  // Objednatel
  const objednatelMatch = text.match(/(?:objednatel|z√°kazn√≠k|customer):?\s*([^\n]+)/i);
  if (objednatelMatch) {
    customFields.objednatel_nazev = objednatelMatch[1].trim();
  }

  // Dodac√≠ adresa
  const dodaciMatch = text.match(/(?:dodac√≠ adresa|delivery address):?\s*([^\n]+(?:\n[^\n]+)?)/i);
  if (dodaciMatch) {
    customFields.dodaci_adresa = dodaciMatch[1].trim().replace(/\n/g, ', ');
  }

  // Celkov√° hodnota
  const totalMatch = text.match(/(?:celkem|total|celkov√° hodnota):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i);
  if (totalMatch) {
    customFields.celkova_hodnota = totalMatch[1].replace(/\s/g, '');
  }
}

else if (docType === 'uctenka') {
  // IDENTIFIKACE
  const receiptMatch = text.match(/(?:√∫ƒçtenka ƒç\.|ƒç√≠slo √∫ƒçtenky|pokladn√≠ doklad):?\s*([A-Z0-9\-\/]+)/i);
  if (receiptMatch) {
    customFields.cislo_uctenky = receiptMatch[1];
  }

  // Datum a ƒças
  const dateTimeMatch = text.match(/(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})\s+(\d{1,2}:\d{2}(?::\d{2})?)/i);
  if (dateTimeMatch) {
    customFields.datum = dateTimeMatch[1];
    customFields.cas = dateTimeMatch[2];
  }

  // Prodejce
  const sellerMatch = text.match(/(?:prodejce|firma|provozovna):?\s*([^\n]+)/i);
  if (sellerMatch) {
    customFields.nazev_prodejce = sellerMatch[1].trim();
  }

  // IƒåO prodejce
  const icoSellerMatch = text.match(/IƒåO?:?\s*(\d{8})/i);
  if (icoSellerMatch) {
    customFields.ico_prodejce = icoSellerMatch[1];
  }

  // EET
  const fikMatch = text.match(/FIK:?\s*([A-Z0-9\-]+)/i);
  if (fikMatch) {
    customFields.fik_kod = fikMatch[1];
    tags.push('eet');
  }

  const bkpMatch = text.match(/BKP:?\s*([A-Z0-9\-]+)/i);
  if (bkpMatch) {
    customFields.bkp_kod = bkpMatch[1];
  }

  const pkpMatch = text.match(/PKP:?\s*([A-Z0-9\-]+)/i);
  if (pkpMatch) {
    customFields.pkp_kod = pkpMatch[1];
  }

  // Pokladna
  const cashRegMatch = text.match(/(?:pokladna|kasa|terminal|pos):?\s*([\d\w]+)/i);
  if (cashRegMatch) {
    customFields.cislo_pokladny = cashRegMatch[1];
  }

  // Celkov√° ƒç√°stka
  const amountMatch = text.match(/(?:celkem|total|suma|k platbƒõ):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk)/i);
  if (amountMatch) {
