}

else if (docType === 'mzdovy_list') {
  // IDENTIFIKACE
  const periodMatch = text.match(/(?:za mƒõs√≠c|obdob√≠|period):?\s*([^\n]+)/i);
  if (periodMatch) {
    customFields.mesic_rok = periodMatch[1].trim();
