}

// === VALIDACE A WORKFLOW TRIGGERS ===
const validationErrors = [];
const workflowTriggers = [];

// Kontrola povinn√Ωch pol√≠ pro fakturu
if (docType === 'faktura') {
  if (!customFields.cislo_faktury) validationErrors.push('Chyb√≠ ƒç√≠slo faktury');
  if (!customFields.dodavatel_ico) validationErrors.push('Chyb√≠ IƒåO dodavatele');
  if (!customFields.castka_celkem_s_dph) validationErrors.push('Chyb√≠ celkov√° ƒç√°stka');
  if (!customFields.datum_vystaveni) validationErrors.push('Chyb√≠ datum vystaven√≠');

  // Workflow triggers
  if (customFields.datum_splatnosti) {
    const dueDate = new Date(customFields.datum_splatnosti.split(/[.\-]/).reverse().join('-'));
    const today = new Date();
    const diffDays = Math.ceil((dueDate - today) / (1000 * 60 * 60 * 24));

    if (diffDays < 0) {
      workflowTriggers.push('po_splatnosti');
      tags.push('urgentni');
    } else if (diffDays <= 7) {
      workflowTriggers.push('blizi_se_splatnost');
      tags.push('dulezite');
    }
  }

  if (customFields.castka_celkem_s_dph) {
    const amount = parseFloat(customFields.castka_celkem_s_dph.replace(',', '.'));
    if (amount > 100000) {
      workflowTriggers.push('vysoka_castka');
      tags.push('ke-schvaleni');
    }
  }
}

