  // PRODEJCE
  const sellerPatterns = [
    /(?:prodejce|firma|provozovna):?\s*([^\n]+)/i,
    /^([A-Z√Åƒåƒé√âƒö√ç≈á√ì≈ò≈†≈§√ö≈Æ√ù≈Ω][^\n]+s\.r\.o\.|a\.s\.|v\.o\.s\.)/im
