  // EET K√ìDY
  const fikMatch = text.match(/FIK:?\s*([A-Z0-9\-]+)/i);
  if (fikMatch) {
    customFields.fik_kod = fikMatch[1];
    tags.push('eet');
  }

  const bkpMatch = text.match(/BKP:?\s*([A-Z0-9\-]+)/i);
  if (bkpMatch) {
    customFields.bkp_kod = bkpMatch[1];
  }

  const pkpMatch = text.match(/PKP:?\s*([A-Z0-9\-]+)/i);
  if (pkpMatch) {
    customFields.pkp_kod = pkpMatch[1];
  }

  // POKLADNA
  const cashRegMatch = text.match(/(?:pokladna|kasa|terminal|pos):?\s*([\d\w]+)/i);
  if (cashRegMatch) {
    customFields.cislo_pokladny = cashRegMatch[1];
  }

  // CELKOV√Å ƒå√ÅSTKA
  const amountMatch = text.match(/(?:celkem|total|suma|k platbƒõ):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk)/i);
  if (amountMatch) {
    customFields.celkova_castka = amountMatch[1].replace(/\s/g, '');
  }

  // ZP≈ÆSOB PLATBY
  if (textLower.includes('hotovƒõ') || textLower.includes('hotovost')) {
    customFields.zpusob_platby = 'Hotovƒõ';
  } else if (textLower.includes('kartou') || textLower.includes('card')) {
    customFields.zpusob_platby = 'Kartou';
  }
}

else if (docType === 'smlouva') {
  // IDENTIFIKACE
  const contractMatch = text.match(/(?:smlouva ƒç\.|ƒç√≠slo smlouvy|contract number):?\s*([A-Z0-9\-\/]+)/i);
  if (contractMatch) {
    customFields.cislo_smlouvy = contractMatch[1];
    tags.push('smlouva-' + contractMatch[1]);
  }

  // TYP SMLOUVY
  const contractTypes = [
    {type: 'Kupn√≠', patterns: ['kupn√≠ smlouva', 'purchase agreement']},
    {type: 'N√°jemn√≠', patterns: ['n√°jemn√≠ smlouva', 'rental agreement', 'lease']},
    {type: 'Servisn√≠', patterns: ['servisn√≠ smlouva', 'service agreement']},
    {type: 'Pracovn√≠', patterns: ['pracovn√≠ smlouva', 'employment contract']},
    {type: 'Dodavatelsk√°', patterns: ['dodavatelsk√° smlouva', 'supply agreement']}
  ];

  for (const ct of contractTypes) {
    if (ct.patterns.some(pattern => textLower.includes(pattern))) {
      customFields.typ_smlouvy = ct.type;
      tags.push(ct.type.toLowerCase() + '-smlouva');
      break;
    }
  }

  // SMLUVN√ç STRANY
  const stranyMatch = text.match(/(?:smluvn√≠ strany|mezi):?\s*([^\n]+(?:\n[^\n]+)?)/i);
  if (stranyMatch) {
    customFields.smluvni_strany = stranyMatch[1].trim();
  }

  // DATUMY
  const signDateMatch = text.match(/(?:uzav≈ôeno dne|signed on|datum uzav≈ôen√≠):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (signDateMatch) {
    customFields.datum_uzavreni = signDateMatch[1];
  }

  const validFromMatch = text.match(/(?:platnost od|valid from|√∫ƒçinnost od):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (validFromMatch) {
    customFields.platnost_od = validFromMatch[1];
  }

  const validToMatch = text.match(/(?:platnost do|valid to|√∫ƒçinnost do):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (validToMatch) {
    customFields.platnost_do = validToMatch[1];
  }

  // V√ùPOVƒöDN√ç LH≈ÆTA
  const vypovMatch = text.match(/(?:v√Ωpovƒõdn√≠ lh≈Øta|notice period):?\s*([^\n]+)/i);
  if (vypovMatch) {
    customFields.vypovedni_lhuta = vypovMatch[1].trim();
  }

  // P≈òEDMƒöT SMLOUVY
  const subjectMatch = text.match(/(?:p≈ôedmƒõt smlouvy|subject):?\s*([^\n]+)/i);
  if (subjectMatch) {
    customFields.predmet_smlouvy = subjectMatch[1].trim();
  }

  // HODNOTA SMLOUVY
  const valueMatch = text.match(/(?:hodnota smlouvy|celkov√° hodnota|contract value):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i);
  if (valueMatch) {
    customFields.celkova_hodnota = valueMatch[1].replace(/\s/g, '');
  }
}

else if (docType === 'objednavka') {
  // IDENTIFIKACE
  const orderMatch = text.match(/(?:objedn√°vka ƒç\.|ƒç√≠slo objedn√°vky|order number):?\s*([A-Z0-9\-\/]+)/i);
  if (orderMatch) {
    customFields.cislo_objednavky = orderMatch[1];
    tags.push('objednavka-' + orderMatch[1]);
  }

  // DATUMY
  const orderDateMatch = text.match(/(?:datum objedn√°vky|order date|objedn√°no):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (orderDateMatch) {
    customFields.datum_objednavky = orderDateMatch[1];
  }

  const deliveryMatch = text.match(/(?:term√≠n dod√°n√≠|delivery date|doruƒçen√≠):?\s*(\d{1,2}[.\-]\d{1,2}[.\-]\d{4})/i);
  if (deliveryMatch) {
    customFields.termin_dodani = deliveryMatch[1];
  }

  // OBJEDNATEL
  const objednatelMatch = text.match(/(?:objednatel|z√°kazn√≠k|customer):?\s*([^\n]+)/i);
  if (objednatelMatch) {
    customFields.objednatel_nazev = objednatelMatch[1].trim();
  }

  // DODAC√ç ADRESA
  const dodaciMatch = text.match(/(?:dodac√≠ adresa|delivery address):?\s*([^\n]+(?:\n[^\n]+)?)/i);
  if (dodaciMatch) {
    customFields.dodaci_adresa = dodaciMatch[1].trim().replace(/\n/g, ', ');
  }

  // CELKOV√Å HODNOTA
  const totalMatch = text.match(/(?:celkem|total|celkov√° hodnota):?\s*([\d\s]+[,.]?\d*)\s*(?:kƒç|czk|eur)/i);
  if (totalMatch) {
    customFields.celkova_hodnota = totalMatch[1].replace(/\s/g, '');
  }
}

// === OBECN√â TAGY ===
tags.push(docType);

// Kategorie podle typu
if (docType === 'faktura' || docType === 'uctenka') tags.push('naklady');
if (docType === 'vypis') tags.push('finance');
if (docType === 'smlouva') tags.push('pravni');
if (docType === 'objednavka') tags.push('nakup');
if (docType === 'newsletter' || docType === 'reklama') tags.push('marketing');

// Kvart√°l (pokud m√°me mƒõs√≠c)
if (customFields.mesic) {
  const monthNames = Object.keys(months);
  const monthIndex = monthNames.indexOf(customFields.mesic);
  if (monthIndex !== -1) {
    const monthNum = monthIndex + 1;
    if (monthNum <= 3) tags.push('Q1');
    else if (monthNum <= 6) tags.push('Q2');
    else if (monthNum <= 9) tags.push('Q3');
    else tags.push('Q4');
  }
}

// Urƒçen√≠ korespondenta
let correspondent = customFields.dodavatel_nazev || 
                   customFields.nazev_banky || 
                   customFields.nazev_prodejce || 
                   customFields.objednatel_nazev || 
                   'Unknown';

// Vytvo≈ôen√≠ titulu dokumentu
let docTitle = '';
if (docType === 'faktura' && customFields.cislo_faktury) {
  docTitle = `Faktura ${customFields.cislo_faktury} - ${correspondent}`;
} else if (docType === 'vypis' && customFields.nazev_banky) {
  docTitle = `V√Ωpis ${customFields.cislo_vypisu || ''} - ${customFields.nazev_banky}`;
} else if (docType === 'smlouva' && customFields.cislo_smlouvy) {
  docTitle = `Smlouva ${customFields.cislo_smlouvy} - ${customFields.typ_smlouvy || ''}`;
} else if (docType === 'objednavka' && customFields.cislo_objednavky) {
  docTitle = `Objedn√°vka ${customFields.cislo_objednavky}`;
} else if (docType === 'uctenka' && customFields.datum) {
  docTitle = `√öƒçtenka ${customFields.datum} - ${correspondent}`;
} else {
  docTitle = `${docType.charAt(0).toUpperCase() + docType.slice(1)} - ${filename}`;
}

// === FIN√ÅLN√ç V√ùSLEDEK ===
return [{
  json: {
    // P≈Øvodn√≠ data
    filepath: filepath,
