    },

    // Souhrn pro zobrazen√≠
    summary: {
      'üìÑ Dokument': docTitle,
      'üìë Typ': docType,
      'üë§ Korespondent': correspondent,
      'üí∞ ƒå√°stka': customFields.castka_celkem_s_dph || customFields.celkova_castka || customFields.celkova_hodnota || customFields.konecny_zustatek || 'N/A',
      'üìÖ Datum': customFields.datum_vystaveni || customFields.datum || customFields.datum_uzavreni || 'N/A',
      'üî¢ ƒå√≠slo': customFields.cislo_faktury || customFields.cislo_smlouvy || customFields.cislo_objednavky || customFields.cislo_vypisu || customFields.cislo_uctenky || 'N/A',
      '‚úÖ Konfidence': confidence + '%',
      'üìù Pol√≠': Object.keys(customFields).length,
      'üè∑Ô∏è Tag≈Ø': [...new Set(tags)].length
    }
  }
}];
