// Validation Engine pro obchodn√≠ dokumenty
// Implementuje business rules m√≠sto keyword matchingu

const content = items[0].binary.data ? Buffer.from(items[0].binary.data, 'base64').toString('utf-8') : '';
const filename = items[0].json.selectedFile?.split('/').pop() || 'unknown.pdf';

// Validaƒçn√≠ pravidla pro ka≈æd√Ω typ dokumentu
const validationRules = {
  faktura: {
    requiredFields: 4,
    mandatoryPatterns: [
      /(?:faktura|invoice)/i,
      /\d+[,.]?\d*\s*k?ƒç/i
    ],
    forbiddenPatterns: [
      /(?:s\s*pozdravem|best\s*regards)/i,
      /(?:p≈ô√≠loha|attachment)/i,
      /(?:zas√≠l√°me\s*v√°m|pos√≠l√°me)/i
    ],
    fieldPatterns: {
      cislo_faktury: /(?:faktura|invoice)\s*ƒç\.?\s*:?\s*([A-Z0-9\-\/]+)/i,
      datum_vystaveni: /datum\s*vystaven√≠\s*:?\s*(\d{1,2}[.\-\/]\d{1,2}[.\-\/]\d{4})/i,
      celkova_castka: /(?:celkem|total|k\s*√∫hradƒõ)\s*:?\s*([\d\s,]+[,.]?\d*)\s*k?ƒç/i,
      ico: /iƒço?\s*:?\s*(\d{8})/i,
      dic: /diƒç?\s*:?\s*(cz\d{8,10})/i,
      vs: /(?:variabiln√≠\s*symbol|vs)\s*:?\s*(\d+)/i
    },
    confidenceThreshold: 0.7
  },

  bankovni_vypis: {
    requiredFields: 5,
    mandatoryPatterns: [
      /(?:v√Ωpis|statement)/i,
      /(?:z≈Østatek|balance)/i,
      /(?:transakce|pohyb|operace)/i,
      /\d{2,6}[\-\/]?\d{4}/
    ],
    forbiddenPatterns: [
      /(?:s\s*pozdravem|best\s*regards)/i,
      /(?:zas√≠l√°me\s*v√Ωpis|pos√≠l√°me)/i,
      /(?:v\s*p≈ô√≠loze|attachment)/i
    ],
    fieldPatterns: {
      cislo_uctu: /(?:ƒç√≠slo\s*√∫ƒçtu|√∫ƒçet)\s*:?\s*([\d\-\/\s]+)/i,
      obdobi_od: /obdob√≠\s*od\s*:?\s*(\d{1,2}[.\-\/]\d{1,2}[.\-\/]\d{4})/i,
      obdobi_do: /obdob√≠\s*do\s*:?\s*(\d{1,2}[.\-\/]\d{1,2}[.\-\/]\d{4})/i,
      pocatecni_zustatek: /poƒç√°teƒçn√≠\s*z≈Østatek\s*:?\s*([-]?[\d\s,]+[,.]?\d*)\s*k?ƒç/i,
      konecny_zustatek: /koneƒçn√Ω\s*z≈Østatek\s*:?\s*([-]?[\d\s,]+[,.]?\d*)\s*k?ƒç/i
    },
    confidenceThreshold: 0.8
  },

  uvodni_dopis: {
    requiredFields: 1,
    mandatoryPatterns: [
      /(?:s\s*pozdravem|best\s*regards|dear|v√°≈æen[√Ω√°])/i
    ],
    forbiddenPatterns: [],
    fieldPatterns: {
      predmet: /(?:p≈ôedmƒõt|subject)\s*:?\s*(.+?)(?:\n|$)/i,
      datum: /(\d{1,2}[.\-\/]\d{1,2}[.\-\/]\d{4})/
    },
    confidenceThreshold: 0.5
  }
};

// Funkce pro validaci dokumentu
function validateDocument(content, rules) {
  let score = 0;
  let extractedFields = {};
  let failedTests = [];

  // 1. Test povinn√Ωch vzor≈Ø
  let mandatoryPassed = 0;
  for (const pattern of rules.mandatoryPatterns) {
    if (pattern.test(content)) {
      mandatoryPassed++;
    } else {
      failedTests.push(`Missing mandatory pattern: ${pattern}`);
    }
  }

  // Pokud chyb√≠ povinn√© vzory, confidence = 0
  if (mandatoryPassed < rules.mandatoryPatterns.length) {
    return { confidence: 0, fields: {}, failedTests };
  }

  // 2. Test zak√°zan√Ωch vzor≈Ø
  for (const pattern of rules.forbiddenPatterns) {
    if (pattern.test(content)) {
      failedTests.push(`Contains forbidden pattern: ${pattern}`);
      return { confidence: 0, fields: {}, failedTests };
    }
  }

  // 3. Extrakce pol√≠
  let fieldCount = 0;
  for (const [fieldName, pattern] of Object.entries(rules.fieldPatterns)) {
    const match = content.match(pattern);
    if (match) {
      extractedFields[fieldName] = match[1]?.trim();
      fieldCount++;
    }
  }

  // 4. V√Ωpoƒçet confidence
  const mandatoryWeight = 0.6;
  const fieldWeight = 0.4;

  const mandatoryConfidence = mandatoryPassed / rules.mandatoryPatterns.length;
  let fieldConfidence = fieldCount / Object.keys(rules.fieldPatterns).length;

  // Penalizace za nedostatek pol√≠
  if (fieldCount < rules.requiredFields) {
    fieldConfidence *= 0.5;
    failedTests.push(`Only ${fieldCount} fields found, required: ${rules.requiredFields}`);
  }

  const confidence = (mandatoryConfidence * mandatoryWeight) + (fieldConfidence * fieldWeight);

  return { confidence, fields: extractedFields, failedTests };
}

// Struktur√°ln√≠ anal√Ωza dokumentu
function analyzeStructure(content) {
  return {
    length: content.length,
    lines: content.split('\n').length,
    words: content.split(/\s+/).length,
    hasNumbers: /\d/.test(content),
    hasDates: /\d{1,2}[.\-\/]\d{1,2}[.\-\/]\d{4}/.test(content),
    hasAmounts: /\d+[,.]?\d*\s*k?ƒç/i.test(content),
    hasICO: /iƒço?\s*:?\s*\d{8}/i.test(content),
    hasAccount: /\d{2,6}[\-\/]?\d{4}/.test(content),
    isLetter: /(?:s\s*pozdravem|dear|v√°≈æen)/i.test(content),
    isAttachmentRef: /(?:v\s*p≈ô√≠loze|p≈ô√≠loha|attachment)/i.test(content),
    hasSignature: /(?:podpis|signature)/i.test(content)
  };
}

// Hlavn√≠ klasifikace
let results = [];

for (const [docType, rules] of Object.entries(validationRules)) {
  const validation = validateDocument(content, rules);
  results.push({
    type: docType,
    confidence: validation.confidence,
    fields: validation.fields,
    failedTests: validation.failedTests
  });
}

// Se≈ôadit podle confidence
results.sort((a, b) => b.confidence - a.confidence);

const bestMatch = results[0];
const structure = analyzeStructure(content);

// Urƒçen√≠ fin√°ln√≠ho typu
let finalType = 'neznamy';
let finalConfidence = bestMatch.confidence;
let finalFields = bestMatch.fields;

if (bestMatch.confidence >= validationRules[bestMatch.type].confidenceThreshold) {
  finalType = bestMatch.type;
} else if (structure.isLetter && structure.isAttachmentRef) {
  finalType = 'uvodni_dopis';
  finalConfidence = 0.9;
}

// P≈ô√≠prava v√Ωstupu
return [
  {
    json: {
      filename: filename,
      documentType: finalType,
      confidence: finalConfidence,
      extractedFields: finalFields,
      structure: structure,
      validationResults: results,
      timestamp: new Date().toISOString(),
      validationEngine: 'Business Rules v2.0'
    }
  }
];

// Extrakce textu z PDF pomoc√≠ lok√°ln√≠ch n√°stroj≈Ø
// Fallback kdy≈æ AnyParser nen√≠ dostupn√Ω

const { exec } = require('child_process');
const util = require('util');
const execAsync = util.promisify(exec);
const fs = require('fs').promises;
const path = require('path');

const filepath = items[0].json.selectedFile;
const filename = path.basename(filepath);

try {
  // Pokus o extrakci textu pomoc√≠ pdftotext
  const { stdout, stderr } = await execAsync(`pdftotext -layout "${filepath}" -`, {
    maxBuffer: 10 * 1024 * 1024 // 10MB buffer
  });

  const extractedText = stdout || '';

  // Pokud text je pr√°zdn√Ω, zkus OCR
  let finalText = extractedText;
  if (extractedText.length < 100) {
    console.log('Text p≈ô√≠li≈° kr√°tk√Ω, zkou≈°√≠m OCR...');

    // Konverze PDF na obr√°zek a OCR
    const tempImage = `/tmp/${filename}.png`;
    await execAsync(`convert -density 300 "${filepath}[0]" "${tempImage}"`);

    const { stdout: ocrText } = await execAsync(`tesseract "${tempImage}" - -l ces+eng`, {
      maxBuffer: 10 * 1024 * 1024
    });

    finalText = ocrText || extractedText;

    // Sma≈æ doƒçasn√Ω soubor
    try {
      await fs.unlink(tempImage);
    } catch (e) {}
  }

  return [
    {
      json: {
        filename: filename,
        filepath: filepath,
        extractedText: finalText,
        textLength: finalText.length,
        extractionMethod: finalText.length > extractedText.length ? 'OCR' : 'pdftotext'
      },
      binary: {
        data: Buffer.from(finalText).toString('base64')
      }
    }
  ];

} catch (error) {
  return [
    {
      json: {
        filename: filename,
        filepath: filepath,
        error: error.message,
        extractedText: '',
        extractionMethod: 'failed'
      }
    }
  ];
}

// P≈ô√≠prava dat pro Paperless API
// Mapov√°n√≠ validation engine v√Ωstupu na Paperless form√°t

const validation = items[0].json;
const fields = validation.extractedFields || {};

// P≈ô√≠prava tag≈Ø podle typu dokumentu a extrahovan√Ωch pol√≠
let tags = [validation.documentType];

// P≈ôidej tagy podle obsahu
if (fields.ico) tags.push(`ico-${fields.ico}`);
if (fields.dic) tags.push(`dic-${fields.dic}`);
if (fields.vs) tags.push(`vs-${fields.vs}`);
if (fields.cislo_faktury) tags.push(`inv-${fields.cislo_faktury}`);
if (fields.cislo_uctu) tags.push(`account-${fields.cislo_uctu.replace(/[\s\/]/g, '')}`);

// P≈ôidej tagy podle confidence
if (validation.confidence >= 0.9) tags.push('high-confidence');
else if (validation.confidence >= 0.7) tags.push('medium-confidence');
else tags.push('low-confidence');

// P≈ôidej struktur√°ln√≠ tagy
if (validation.structure.isLetter) tags.push('letter');
if (validation.structure.isAttachmentRef) tags.push('has-attachment');

// Urƒçen√≠ korespondenta podle n√°zvu souboru nebo obsahu
let correspondent = 'Unknown';
if (validation.filename.includes('MBW')) correspondent = 'MyBrainWorks s.r.o.';
else if (fields.dodavatel) correspondent = fields.dodavatel;

// Sestaven√≠ pozn√°mky s extrahovan√Ωmi daty
let notes = `Validation Engine v2.0\n`;
notes += `Document Type: ${validation.documentType}\n`;
notes += `Confidence: ${(validation.confidence * 100).toFixed(1)}%\n`;
notes += `\nExtracted Fields:\n`;

for (const [key, value] of Object.entries(fields)) {
  notes += `- ${key}: ${value}\n`;
}

notes += `\nValidation Results:\n`;
for (const result of validation.validationResults) {
  notes += `- ${result.type}: ${(result.confidence * 100).toFixed(1)}%\n`;
  if (result.failedTests.length > 0) {
    notes += `  Failed: ${result.failedTests[0]}\n`;
  }
}

return [
  {
    json: {
      title: `${validation.documentType.toUpperCase()} - ${validation.filename}`,
      document_type: validation.documentType,
      correspondent: correspondent,
      tags: tags.join(','),
      notes: notes,
      custom_fields: fields,
      confidence: validation.confidence,
      filename: validation.filename
    }
  }
];

// Fin√°ln√≠ statistiky a report

const validation = items[0].json;
const paperlessData = items[1]?.json || {};

const report = {
  summary: {
    filename: validation.filename,
    documentType: validation.documentType,
    confidence: `${(validation.confidence * 100).toFixed(1)}%`,
    extractedFields: Object.keys(validation.extractedFields || {}).length,
    validationEngine: 'Business Rules v2.0'
  },

  classification: {
    detected: validation.documentType,
    isCorrect: validation.confidence >= 0.7,
    reason: validation.confidence < 0.7 ? 
      'Low confidence - manual review recommended' : 
      'High confidence - automated classification'
  },

  extractedData: validation.extractedFields || {},

  structuralAnalysis: {
    isLetter: validation.structure.isLetter,
    isAttachmentReference: validation.structure.isAttachmentRef,
    hasSignature: validation.structure.hasSignature,
    documentLength: validation.structure.length,
    hasICO: validation.structure.hasICO,
    hasAmounts: validation.structure.hasAmounts
  },

  validationDetails: validation.validationResults.map(r => ({
    type: r.type,
    confidence: `${(r.confidence * 100).toFixed(1)}%`,
    fieldsFound: Object.keys(r.fields).length,
    issues: r.failedTests.length > 0 ? r.failedTests[0] : 'None'
  })),

  paperlessUpload: paperlessData.id ? {
    status: 'Success',
    documentId: paperlessData.id,
    url: `http://localhost:8050/documents/${paperlessData.id}/`
  } : {
    status: 'Pending',
    message: 'Document ready for upload'
  },

  processingTime: new Date().toISOString()
};

// V√Ωstup pro zobrazen√≠
console.log('\n' + '='.repeat(60));
console.log('üìä VALIDATION ENGINE REPORT');
console.log('='.repeat(60));
console.log(`üìÑ File: ${report.summary.filename}`);
console.log(`üìù Type: ${report.summary.documentType} (${report.summary.confidence})`);
console.log(`‚úÖ Classification: ${report.classification.isCorrect ? 'CORRECT' : 'NEEDS REVIEW'}`);
console.log(`üîç Extracted Fields: ${report.summary.extractedFields}`);

if (Object.keys(report.extractedData).length > 0) {
  console.log('\nüìã Extracted Data:');
  for (const [key, value] of Object.entries(report.extractedData)) {
    console.log(`   ${key}: ${value}`);
  }
}

console.log('\nüìä Validation Scores:');
for (const detail of report.validationDetails) {
  console.log(`   ${detail.type}: ${detail.confidence}`);
}

console.log('='.repeat(60));

return [
  {
    json: report
  }
];
